const n=document.getElementById("quote");document.getElementById("randomButton").addEventListener("click",(async function(){try{const s=await async function(n){const a=await fetch(n);return await a.json()}("data/quotes.json");let c=(t=0,o=s.length,Math.floor(Math.random()*(o-t+1)+t));a=s[`${c}`],n.innerHTML=`\n    <div class="card card-body mb-3">\n      <div class="row">\n        <div class="col-md-3">\n          <img src="${a.cover}" alt="" class="img-fluid mb-2"/>\n          <a href="${a.link}" target="_blank" class="btn btn-primary btn-block mb-4">Ecouter le morceau</a>\n        </div>\n        <div class="col-md-9">\n          <span class="badge badge-danger">Morceau: <strong>${a.song_name}</strong></span>\n          <span class="badge badge-success">Album: <strong>${a.album_name}</strong></span>\n          <br><br>\n          <blockquote class="blockquote">\n          <p class="mb-0">${a.body}.</p>\n          <footer class="blockquote-footer"><cite>${a.author}</cite></footer>\n        </blockquote>\n        </div>\n      </div>\n    </div>\n  `}catch(s){console.error(s)}var a;var t,o}));
